import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import AuthContext from '../context/AuthContext';
import './OnboardingPage.css';

const OnboardingPage = () => {
  const [step, setStep] = useState(1);
  const [skillCategories, setSkillCategories] = useState({});
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedSkills, setSelectedSkills] = useState([]);
  const navigate = useNavigate();

  // --- Get the new reloadUser function from our context ---
  const { reloadUser } = useContext(AuthContext);

  useEffect(() => {
    const fetchSkills = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/skills');
        const groupedSkills = res.data.reduce((acc, skill) => {
          const { category } = skill;
          if (!acc[category]) acc[category] = [];
          acc[category].push(skill.name);
          return acc;
        }, {});
        setSkillCategories(groupedSkills);
        setLoading(false);
      } catch (err) {
        console.error("Failed to fetch skills", err);
        setLoading(false);
      }
    };
    fetchSkills();
  }, []);

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setStep(2);
  };

  const handleSkillToggle = (skill) => {
    setSelectedSkills((prev) =>
      prev.includes(skill) ? prev.filter((s) => s !== skill) : [...prev, skill]
    );
  };

  const handleSubmit = async () => {
    if (selectedSkills.length === 0) {
      alert('Please select at least one skill.');
      return;
    }
    try {
      // 1. Save the skills to the database
      await axios.put('http://localhost:5000/api/users/onboard', {
        skillsToTeach: selectedSkills,
      });

      // --- THIS IS THE FIX ---
      // 2. Force the app to reload the user's data from the server
      await reloadUser();

      // 3. NOW navigate to the dashboard
      navigate('/dashboard');
    } catch (err) {
      console.error(err);
      alert('Failed to save skills. Please try again.');
    }
  };

  if (loading) {
    return <div className="onboarding-container"><p>Loading Skills...</p></div>;
  }

  return (
    <div className="onboarding-container">
      <div className="onboarding-card">
        {step === 1 && (
          <div className="step active">
            <h2><i className="fas fa-layer-group"></i> Choose Your Domain</h2>
            <p>Select your primary area of expertise to get started.</p>
            <div className="category-grid">
              {Object.keys(skillCategories).map((category) => (
                <div key={category} className="category-card" onClick={() => handleCategorySelect(category)}>
                  <h4>{category}</h4>
                </div>
              ))}
            </div>
          </div>
        )}
        {step === 2 && (
          <div className="step active">
            <h2><i className="fas fa-star"></i> Select Your Skills</h2>
            <p>Choose the skills you are confident in teaching. You can change these later.</p>
            <div className="skills-grid">
              {skillCategories[selectedCategory].map((skill) => (
                <div
                  key={skill}
                  className={`skill-checkbox ${selectedSkills.includes(skill) ? 'selected' : ''}`}
                  onClick={() => handleSkillToggle(skill)}
                >
                  {skill}
                </div>
              ))}
            </div>
            <div className="onboarding-actions">
              <button className="back-button" onClick={() => setStep(1)}>Back</button>
              <button className="finish-button" onClick={handleSubmit}>Finish & Go to Dashboard</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default OnboardingPage;
