import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import AuthContext from '../context/AuthContext';
import './AuthPage.css';

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ name: '', email: '', password: '' });
  const { login, user } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    if (user) {
      navigate('/dashboard');
    }
  }, [user, navigate]);

  const { name, email, password } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    if (isLogin) {
      try {
        await login(email, password);
        // The useEffect hook will handle the redirect
      } catch (err) {
        alert('Login failed. Please check your credentials.');
        console.error(err);
      }
    } else {
      try {
        await axios.post('http://localhost:5000/api/auth/register', { name, email, password });
        alert('Registration successful! Please login.');
        setIsLogin(true);
      } catch (err) {
        alert('Registration failed. The email might already be in use.');
        console.error(err);
      }
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h2><i className={isLogin ? 'fas fa-sign-in-alt' : 'fas fa-user-plus'}></i>{isLogin ? 'Login' : 'Register'}</h2>
        <form onSubmit={onSubmit}>
          {!isLogin && (<div className="form-group"><label>Name</label><input type="text" name="name" value={name} onChange={onChange} required /></div>)}
          <div className="form-group"><label>Email</label><input type="email" name="email" value={email} onChange={onChange} required /></div>
          <div className="form-group"><label>Password</label><input type="password" name="password" value={password} onChange={onChange} required minLength="6" /></div>
          <button type="submit" className="auth-button">{isLogin ? 'Login' : 'Register'}</button>
        </form>
        <button onClick={() => setIsLogin(!isLogin)} className="toggle-button">{isLogin ? 'Need an account? Register' : 'Have an account? Login'}</button>
      </div>
    </div>
  );
};

export default AuthPage;
