import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import AuthContext from '../context/AuthContext';
import './OnboardingPage.css';

const AVAILABILITY_OPTIONS = ["Weekdays (Morning)", "Weekdays (Afternoon)", "Weekdays (Evening)", "Weekends (Morning)", "Weekends (Afternoon)"];

const OnboardingPage = () => {
  const [step, setStep] = useState(1);
  const [skillCategories, setSkillCategories] = useState({});
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedSkills, setSelectedSkills] = useState([]);
  const [selectedAvailability, setSelectedAvailability] = useState([]); // New state for availability
  const navigate = useNavigate();
  const { reloadUser } = useContext(AuthContext);

  useEffect(() => {
    // Fetch skills logic remains the same
  }, []);

  const handleSkillToggle = (skill) => {
    setSelectedSkills((prev) => prev.includes(skill) ? prev.filter((s) => s !== skill) : [...prev, skill]);
  };
  
  const handleAvailabilityToggle = (option) => {
    setSelectedAvailability((prev) => prev.includes(option) ? prev.filter((a) => a !== option) : [...prev, option]);
  };

  const handleSubmit = async () => {
    if (selectedSkills.length === 0 || selectedAvailability.length === 0) {
      alert('Please select at least one skill and one availability option.');
      return;
    }
    try {
      await axios.put('http://localhost:5000/api/users/onboard', {
        skillsToTeach: selectedSkills,
        availability: selectedAvailability,
      });
      await reloadUser();
      navigate('/dashboard');
    } catch (err) {
      alert('Failed to save your profile. Please try again.');
    }
  };

  // ... other functions ...

  return (
    <div className="onboarding-container">
      <div className="onboarding-card">
        {/* Step 1 & 2 JSX remains the same */}
        
        {/* --- NEW: Step 3 for Availability --- */}
        {step === 3 && (
          <div className="step active">
            <h2><i className="fas fa-clock"></i> Set Your Availability</h2>
            <p>Let others know when you are generally free to teach.</p>
            <div className="skills-grid">
              {AVAILABILITY_OPTIONS.map((option) => (
                <div
                  key={option}
                  className={`skill-checkbox ${selectedAvailability.includes(option) ? 'selected' : ''}`}
                  onClick={() => handleAvailabilityToggle(option)}
                >
                  {option}
                </div>
              ))}
            </div>
            <div className="onboarding-actions">
              <button className="back-button" onClick={() => setStep(2)}>Back</button>
              <button className="finish-button" onClick={handleSubmit}>Finish & Go to Dashboard</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default OnboardingPage;
