import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import './DiscoverPage.css'; // The CSS file will also be updated

const DiscoverPage = () => {
  const [mentors, setMentors] = useState([]);
  const [allSkills, setAllSkills] = useState([]);
  const [loading, setLoading] = useState(true);

  // --- NEW: State for our filters ---
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [skillFilter, setSkillFilter] = useState('all');

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        // We can fetch users and skills at the same time
        const [usersRes, skillsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/users'),
          axios.get('http://localhost:5000/api/skills'),
        ]);

        setMentors(usersRes.data);
        setAllSkills(skillsRes.data);
        setLoading(false);
      } catch (err) {
        console.error('Failed to fetch initial data', err);
        setLoading(false);
      }
    };
    fetchInitialData();
  }, []);

  // --- NEW: Filtering Logic ---
  // useMemo is a React hook that recalculates the list only when filters change,
  // which is very efficient.
  const filteredMentors = useMemo(() => {
    return mentors.filter((mentor) => {
      // Category check
      const categoryMatch =
        categoryFilter === 'all' ||
        mentor.skillsToTeach.some((skill) => skill.category === categoryFilter);

      // Skill check
      const skillMatch =
        skillFilter === 'all' ||
        mentor.skillsToTeach.some((skill) => skill.name === skillFilter);

      return categoryMatch && skillMatch;
    });
  }, [mentors, categoryFilter, skillFilter]);

  // Get unique categories from the skills list for the dropdown
  const uniqueCategories = [...new Set(allSkills.map(skill => skill.category))];

  if (loading) {
    return <div className="loading-text">Loading Mentors...</div>;
  }

  return (
    <div className="discover-page-content">
      {/* --- NEW: Filters Section --- */}
      <div className="card filters-card">
        <h3><i className="fas fa-filter"></i> Find Your Perfect Mentor</h3>
        <div className="filters">
          <div className="filter-group">
            <label htmlFor="categoryFilter">Category</label>
            <select
              id="categoryFilter"
              className="filter-select"
              value={categoryFilter}
              onChange={(e) => setCategoryFilter(e.target.value)}
            >
              <option value="all">All Categories</option>
              {uniqueCategories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
          </div>
          <div className="filter-group">
            <label htmlFor="skillFilter">Skill</label>
            <select
              id="skillFilter"
              className="filter-select"
              value={skillFilter}
              onChange={(e) => setSkillFilter(e.target.value)}
            >
              <option value="all">All Skills</option>
              {allSkills.map(skill => (
                <option key={skill._id} value={skill.name}>{skill.name}</option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* --- User Cards Grid --- */}
      <div className="user-cards-grid">
        {filteredMentors.length === 0 ? (
          <div className="no-results-card">
            <p>No mentors found matching your criteria.</p>
          </div>
        ) : (
          filteredMentors.map((user) => (
            <div key={user._id} className="user-card">
              <div className="user-header">
                <div className="user-info-card">
                  <h4>{user.name}</h4>
                  <div className="user-meta">{user.email}</div>
                </div>
                <div className="rating">
                  <i className="fas fa-star"></i>
                  <span>5.0</span>
                </div>
              </div>
              <div className="skills-tags">
                {user.skillsToTeach.map((skill) => (
                  <span key={skill._id} className="skill-tag">
                    {skill.name}
                  </span>
                ))}
              </div>
              <button className="connect-btn">
                <i className="fas fa-calendar-plus"></i>
                Request Session
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default DiscoverPage;
