import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './DiscoverPage.css'; // We will create this CSS file next

const DiscoverPage = () => {
  const [mentors, setMentors] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/users');
        setMentors(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Failed to fetch users', err);
        setLoading(false);
      }
    };
    fetchUsers();
  }, []);

  if (loading) {
    return <div className="loading-text">Loading Mentors...</div>;
  }

  return (
    <div className="discover-container">
      <h1 className="discover-title">
        <i className="fas fa-search"></i> Discover Mentors
      </h1>
      <p className="discover-subtitle">
        Find talented people to swap skills with.
      </p>

      <div className="user-cards-grid">
        {mentors.length === 0 ? (
          <p className="loading-text">No other users have signed up yet. Check back soon!</p>
        ) : (
          mentors.map((user) => (
            <div key={user._id} className="user-card">
              <div className="user-header">
                <div className="user-info-card">
                  <h4>{user.name}</h4>
                  <div className="user-meta">{user.email}</div>
                </div>
                <div className="rating">
                  <i className="fas fa-star"></i>
                  <span>5.0</span>
                </div>
              </div>

              <div className="skills-tags">
                {user.skillsToTeach.map((skill) => (
                  <span key={skill._id} className="skill-tag">
                    {skill.name}
                  </span>
                ))}
              </div>

              <button className="connect-btn">
                <i className="fas fa-calendar-plus"></i>
                Request Session
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default DiscoverPage;
