import React, { useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../context/AuthContext';

const DashboardPage = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    // This logic is now safe because the user object will be fresh
    if (user && user.skillsToTeach && user.skillsToTeach.length === 0) {
      navigate('/onboarding');
    }
  }, [user, navigate]);

  const cardStyle = {
    background: 'var(--bg-card)',
    borderRadius: '16px',
    padding: '2rem',
    border: '1px solid var(--border)',
    textAlign: 'center',
    maxWidth: '600px',
    margin: '5rem auto',
    boxShadow: 'var(--shadow)',
  };

  const buttonStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '0.75rem',
    width: '100%',
    padding: '1rem',
    border: 'none',
    borderRadius: '12px',
    fontWeight: '600',
    cursor: 'pointer',
    background: 'linear-gradient(135deg, #ef4444, #dc2626)',
    color: 'white',
    marginTop: '2rem',
    transition: 'transform 0.2s ease',
  };

  return (
    <div style={{ color: 'white', padding: '2rem' }}>
      <div style={cardStyle}>
        <h1>Welcome, {user?.name}!</h1>
        <p>Your email: {user?.email}</p>
        <p>You have {user?.skillMoney} SkillMoney coins!</p>
        <p style={{ color: 'var(--text-secondary)', marginTop: '1rem' }}>
          {/* --- THIS IS THE CORRECT WAY TO DISPLAY THE SKILLS --- */}
          Your skills: {user?.skillsToTeach?.map(skill => skill.name).join(', ') || 'No skills selected yet.'}
        </p>

        <button
          style={buttonStyle}
          onClick={logout}
          onMouseOver={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
          onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0)'}
        >
          <i className="fas fa-sign-out-alt"></i> Logout
        </button>
      </div>
    </div>
  );
};

export default DashboardPage;
