import React, { useState, useEffect, useMemo, useContext } from 'react';
import axios from 'axios';
import Modal from '../components/Modal';
import AuthContext from '../context/AuthContext'; // Import AuthContext
import './DiscoverPage.css';

const DURATION_OPTIONS = [
  { duration: 30, cost: 25, label: '30 minutes' },
  { duration: 60, cost: 50, label: '1 hour' },
  { duration: 90, cost: 70, label: '1.5 hours' },
];

const DiscoverPage = () => {
  const [mentors, setMentors] = useState([]);
  const [allSkills, setAllSkills] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [skillFilter, setSkillFilter] = useState('all');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedMentor, setSelectedMentor] = useState(null);
  const [requestMessage, setRequestMessage] = useState('');
  const [requestSkillId, setRequestSkillId] = useState('');

  // --- NEW: State for session duration and cost ---
  const [sessionCost, setSessionCost] = useState(DURATION_OPTIONS[0].cost);

  // --- Get reloadUser to update coin balance in the UI ---
  const { reloadUser } = useContext(AuthContext);

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const [usersRes, skillsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/users'),
          axios.get('http://localhost:5000/api/skills'),
        ]);
        setMentors(usersRes.data);
        setAllSkills(skillsRes.data);
        setLoading(false);
      } catch (err) { console.error('Failed to fetch initial data', err); setLoading(false); }
    };
    fetchInitialData();
  }, []);

  const filteredMentors = useMemo(() => {
    return mentors.filter((mentor) => {
      const categoryMatch = categoryFilter === 'all' || mentor.skillsToTeach.some((s) => s.category === categoryFilter);
      const skillMatch = skillFilter === 'all' || mentor.skillsToTeach.some((s) => s.name === skillFilter);
      return categoryMatch && skillMatch;
    });
  }, [mentors, categoryFilter, skillFilter]);

  const openRequestModal = (mentor) => {
    setSelectedMentor(mentor);
    setIsModalOpen(true);
    if (mentor.skillsToTeach.length > 0) setRequestSkillId(mentor.skillsToTeach[0]._id);
    setSessionCost(DURATION_OPTIONS[0].cost); // Reset to default cost
  };

  const closeRequestModal = () => {
    setIsModalOpen(false);
    setSelectedMentor(null);
    setRequestMessage('');
    setRequestSkillId('');
  };

  const handleRequestSubmit = async (e) => {
    e.preventDefault();
    if (!requestMessage || !requestSkillId) {
      alert('Please select a skill and write a message.');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/requests', {
        teacherId: selectedMentor._id,
        skillId: requestSkillId,
        message: requestMessage,
        cost: sessionCost, // Send the cost with the request
      });
      alert('Your request has been sent successfully! The cost has been deducted from your balance.');
      await reloadUser(); // Refresh user data to show new coin balance
      closeRequestModal();
    } catch (err) {
      alert(err.response?.data?.msg || 'There was an error sending your request.');
    }
  };

  const uniqueCategories = [...new Set(allSkills.map(skill => skill.category))];

  if (loading) return <div className="loading-text">Loading Mentors...</div>;

  return (
    <>
      <div className="discover-page-content">
        <div className="card filters-card">
          <h3><i className="fas fa-filter"></i> Find Your Perfect Mentor</h3>
          <div className="filters">
            <div className="filter-group"><label htmlFor="categoryFilter">Category</label><select id="categoryFilter" className="filter-select" value={categoryFilter} onChange={(e) => setCategoryFilter(e.target.value)}><option value="all">All Categories</option>{uniqueCategories.map(c => <option key={c} value={c}>{c}</option>)}</select></div>
            <div className="filter-group"><label htmlFor="skillFilter">Skill</label><select id="skillFilter" className="filter-select" value={skillFilter} onChange={(e) => setSkillFilter(e.target.value)}><option value="all">All Skills</option>{allSkills.map(s => <option key={s._id} value={s.name}>{s.name}</option>)}</select></div>
          </div>
        </div>
        <div className="user-cards-grid">
          {filteredMentors.length === 0 ? (<div className="no-results-card"><p>No mentors found.</p></div>) : (
            filteredMentors.map((user) => (
              <div key={user._id} className="user-card">
                <div className="user-header"><div className="user-info-card"><h4>{user.name}</h4><div className="user-meta">{user.email}</div></div><div className="rating"><i className="fas fa-star"></i><span>5.0</span></div></div>
                <div className="skills-tags">{user.skillsToTeach.map((skill) => <span key={skill._id} className="skill-tag">{skill.name}</span>)}</div>
                <button className="connect-btn" onClick={() => openRequestModal(user)}><i className="fas fa-calendar-plus"></i> Request Session</button>
              </div>
            ))
          )}
        </div>
      </div>

      <Modal isOpen={isModalOpen} onClose={closeRequestModal}>
        {selectedMentor && (
          <form onSubmit={handleRequestSubmit} className="request-form">
            <h2>Request a Session with {selectedMentor.name}</h2>
            <div className="form-group"><label>Skill to learn</label><select className="filter-select" value={requestSkillId} onChange={(e) => setRequestSkillId(e.target.value)}>{selectedMentor.skillsToTeach.map(s => <option key={s._id} value={s._id}>{s.name}</option>)}</select></div>
            
            {/* --- NEW: Duration and Cost Selector --- */}
            <div className="form-group"><label>Session duration</label><select className="filter-select" onChange={(e) => setSessionCost(Number(e.target.value))}>{DURATION_OPTIONS.map(opt => <option key={opt.duration} value={opt.cost}>{opt.label} - {opt.cost} Coins</option>)}</select></div>

            <div className="form-group"><label>Your Message</label><textarea placeholder={`Hi ${selectedMentor.name}, I'd love to learn...`} rows="4" value={requestMessage} onChange={(e) => setRequestMessage(e.target.value)} required></textarea></div>
            <button type="submit" className="connect-btn">Send Request & Spend {sessionCost} Coins</button>
          </form>
        )}
      </Modal>
    </>
  );
};

export default DiscoverPage;
