import React, { useState } from 'react';
import axios from 'axios';
import './AuthPage.css'; // We will create this CSS file next

const AuthPage = () => {
  // State to toggle between Login and Register forms
  const [isLogin, setIsLogin] = useState(true);

  // State to hold the form data (name, email, password)
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  const { name, email, password } = formData;

  // Function to update state when user types in input fields
  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Function to handle form submission
  const onSubmit = async (e) => {
    e.preventDefault(); // Prevent the form from reloading the page

    if (isLogin) {
      // --- LOGIN LOGIC ---
      try {
        const res = await axios.post('http://localhost:5000/api/auth/login', { email, password });
        console.log('Login successful!', res.data);
        // TODO: Save the token and redirect the user
      } catch (err) {
        console.error('Login failed:', err.response.data);
      }
    } else {
      // --- REGISTER LOGIC ---
      try {
        const res = await axios.post('http://localhost:5000/api/auth/register', { name, email, password });
        console.log('Registration successful!', res.data);
        // Switch to login form after successful registration
        setIsLogin(true);
      } catch (err) {
        console.error('Registration failed:', err.response.data);
      }
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h2>{isLogin ? 'Login' : 'Register'}</h2>
        <form onSubmit={onSubmit}>
          {/* Only show the Name field if it's the Register form */}
          {!isLogin && (
            <div className="form-group">
              <label>Name</label>
              <input
                type="text"
                name="name"
                value={name}
                onChange={onChange}
                required
              />
            </div>
          )}
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={email}
              onChange={onChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={password}
              onChange={onChange}
              required
            />
          </div>
          <button type="submit" className="auth-button">
            {isLogin ? 'Login' : 'Register'}
          </button>
        </form>
        <button onClick={() => setIsLogin(!isLogin)} className="toggle-button">
          {isLogin ? 'Need an account? Register' : 'Have an account? Login'}
        </button>
      </div>
    </div>
  );
};

export default AuthPage;
