import React, { useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../context/AuthContext';

const DashboardPage = () => {
  // Get the user and logout function from our global context
  const { user, logout } = useContext(AuthContext);
  // Get the navigate function from React Router to redirect users
  const navigate = useNavigate();

  // --- Smart Redirection Logic ---
  // This effect runs whenever the 'user' object changes.
  useEffect(() => {
    // Check if the user object exists AND if their skillsToTeach array is empty.
    // The `skillsToTeach` array is what we fill during onboarding.
    if (user && user.skillsToTeach && user.skillsToTeach.length === 0) {
      // If they have no skills, they haven't finished onboarding. Redirect them.
      navigate('/onboarding');
    }
  }, [user, navigate]); // The dependencies for this effect

  // --- Inline Styles for the component ---
  const cardStyle = {
    background: 'var(--bg-card)',
    borderRadius: '16px',
    padding: '2rem',
    border: '1px solid var(--border)',
    textAlign: 'center',
    maxWidth: '600px',
    margin: '5rem auto',
    boxShadow: 'var(--shadow)',
  };

  const buttonStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '0.75rem',
    width: '100%',
    padding: '1rem',
    border: 'none',
    borderRadius: '12px',
    fontWeight: '600',
    cursor: 'pointer',
    background: 'linear-gradient(135deg, #ef4444, #dc2626)',
    color: 'white',
    marginTop: '2rem',
    transition: 'transform 0.2s ease',
  };

  // --- JSX to render the page ---
  return (
    <div style={{ color: 'white', padding: '2rem' }}>
      <div style={cardStyle}>
        {/* Using optional chaining (?.) is a safe way to access properties
            in case the user object is momentarily null during loading */}
        <h1>Welcome, {user?.name}!</h1>
        <p>Your email: {user?.email}</p>
        <p>You have {user?.skillMoney} SkillMoney coins!</p>
        <p style={{ color: 'var(--text-secondary)', marginTop: '1rem' }}>
          Your skills: {user?.skillsToTeach?.join(', ') || 'No skills selected yet.'}
        </p>

        <button
          style={buttonStyle}
          onClick={logout}
          onMouseOver={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
          onMouseOut={(e) => e.currentTarget.style.transform = 'translateY(0)'}
        >
          <i className="fas fa-sign-out-alt"></i> Logout
        </button>
      </div>
    </div>
  );
};

export default DashboardPage;
