import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import Modal from '../components/Modal'; // Import our new Modal component
import './DiscoverPage.css';

const DiscoverPage = () => {
  const [mentors, setMentors] = useState([]);
  const [allSkills, setAllSkills] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [skillFilter, setSkillFilter] = useState('all');

  // --- NEW: State for the Modal ---
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedMentor, setSelectedMentor] = useState(null);
  const [requestMessage, setRequestMessage] = useState('');
  const [requestSkillId, setRequestSkillId] = useState('');

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const [usersRes, skillsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/users'),
          axios.get('http://localhost:5000/api/skills'),
        ]);
        setMentors(usersRes.data);
        setAllSkills(skillsRes.data);
        setLoading(false);
      } catch (err) {
        console.error('Failed to fetch initial data', err);
        setLoading(false);
      }
    };
    fetchInitialData();
  }, []);

  const filteredMentors = useMemo(() => {
    return mentors.filter((mentor) => {
      const categoryMatch = categoryFilter === 'all' || mentor.skillsToTeach.some((skill) => skill.category === categoryFilter);
      const skillMatch = skillFilter === 'all' || mentor.skillsToTeach.some((skill) => skill.name === skillFilter);
      return categoryMatch && skillMatch;
    });
  }, [mentors, categoryFilter, skillFilter]);

  // --- NEW: Functions to handle the modal ---
  const openRequestModal = (mentor) => {
    setSelectedMentor(mentor);
    setIsModalOpen(true);
    // Automatically select the first skill of the mentor
    if (mentor.skillsToTeach.length > 0) {
      setRequestSkillId(mentor.skillsToTeach[0]._id);
    }
  };

  const closeRequestModal = () => {
    setIsModalOpen(false);
    setSelectedMentor(null);
    setRequestMessage('');
    setRequestSkillId('');
  };

  const handleRequestSubmit = async (e) => {
    e.preventDefault();
    if (!requestMessage || !requestSkillId) {
      alert('Please select a skill and write a message.');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/requests', {
        teacherId: selectedMentor._id,
        skillId: requestSkillId,
        message: requestMessage,
      });
      alert('Your request has been sent successfully!');
      closeRequestModal();
    } catch (err) {
      console.error('Failed to send request', err);
      alert('There was an error sending your request.');
    }
  };

  const uniqueCategories = [...new Set(allSkills.map(skill => skill.category))];

  if (loading) {
    return <div className="loading-text">Loading Mentors...</div>;
  }

  return (
    <>
      <div className="discover-page-content">
        <div className="card filters-card">
          <h3><i className="fas fa-filter"></i> Find Your Perfect Mentor</h3>
          <div className="filters">
            <div className="filter-group">
              <label htmlFor="categoryFilter">Category</label>
              <select id="categoryFilter" className="filter-select" value={categoryFilter} onChange={(e) => setCategoryFilter(e.target.value)}>
                <option value="all">All Categories</option>
                {uniqueCategories.map(category => <option key={category} value={category}>{category}</option>)}
              </select>
            </div>
            <div className="filter-group">
              <label htmlFor="skillFilter">Skill</label>
              <select id="skillFilter" className="filter-select" value={skillFilter} onChange={(e) => setSkillFilter(e.target.value)}>
                <option value="all">All Skills</option>
                {allSkills.map(skill => <option key={skill._id} value={skill.name}>{skill.name}</option>)}
              </select>
            </div>
          </div>
        </div>

        <div className="user-cards-grid">
          {filteredMentors.length === 0 ? (
            <div className="no-results-card"><p>No mentors found matching your criteria.</p></div>
          ) : (
            filteredMentors.map((user) => (
              <div key={user._id} className="user-card">
                <div className="user-header">
                  <div className="user-info-card"><h4>{user.name}</h4><div className="user-meta">{user.email}</div></div>
                  <div className="rating"><i className="fas fa-star"></i><span>5.0</span></div>
                </div>
                <div className="skills-tags">
                  {user.skillsToTeach.map((skill) => <span key={skill._id} className="skill-tag">{skill.name}</span>)}
                </div>
                <button className="connect-btn" onClick={() => openRequestModal(user)}>
                  <i className="fas fa-calendar-plus"></i> Request Session
                </button>
              </div>
            ))
          )}
        </div>
      </div>

      {/* --- NEW: The Modal for sending a request --- */}
      <Modal isOpen={isModalOpen} onClose={closeRequestModal}>
        {selectedMentor && (
          <form onSubmit={handleRequestSubmit} className="request-form">
            <h2>Request a Session with {selectedMentor.name}</h2>
            <div className="form-group">
              <label>Which skill do you want to learn?</label>
              <select
                className="filter-select"
                value={requestSkillId}
                onChange={(e) => setRequestSkillId(e.target.value)}
              >
                {selectedMentor.skillsToTeach.map(skill => (
                  <option key={skill._id} value={skill._id}>{skill.name}</option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label>Your Message</label>
              <textarea
                placeholder={`Hi ${selectedMentor.name}, I'd love to learn...`}
                rows="4"
                value={requestMessage}
                onChange={(e) => setRequestMessage(e.target.value)}
                required
              ></textarea>
            </div>
            <button type="submit" className="connect-btn">Send Request</button>
          </form>
        )}
      </Modal>
    </>
  );
};

export default DiscoverPage;
