import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';

// Import Pages
import AuthPage from './pages/AuthPage';
import DiscoverPage from './pages/DiscoverPage';
import OnboardingPage from './pages/OnboardingPage';
import RequestsPage from './pages/RequestsPage';
import ProfilePage from './pages/ProfilePage'; // <-- Import ProfilePage
import LandingPage from './pages/LandingPage'; // <-- Import LandingPage

// Import Context, Layout, and PrivateRoute
import AuthContext from './context/AuthContext';
import DashboardLayout from './components/DashboardLayout';
import PrivateRoute from './context/PrivateRoute';

// This is a new "gatekeeper" component for the root path
const HomePage = () => {
  const { user } = useContext(AuthContext);
  // If user is logged in, redirect to dashboard. Otherwise, show landing page.
  return user ? <Navigate to="/dashboard" /> : <LandingPage />;
};

function App() {
  return (
    <Router>
      <div className="App">
        <Routes>
          {/* The root path is now handled by our gatekeeper */}
          <Route path="/" element={<HomePage />} />

          {/* The login/register form now has its own dedicated path */}
          <Route path="/auth" element={<AuthPage />} />

          {/* Private Onboarding Route */}
          <Route
            path="/onboarding"
            element={
              <PrivateRoute>
                <OnboardingPage />
              </PrivateRoute>
            }
          />

          {/* --- NESTED DASHBOARD ROUTES --- */}
          <Route
            path="/dashboard"
            element={
              <PrivateRoute>
                <DashboardLayout />
              </PrivateRoute>
            }
          >
            <Route index element={<DiscoverPage />} />
            <Route path="requests" element={<RequestsPage />} />
            <Route path="profile" element={<ProfilePage />} />
          </Route>
        </Routes>
      </div>
    </Router>
  );
}

export default App;
