import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../context/AuthContext';
import './AuthPage.css';

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });
  const [error, setError] = useState(''); // To display errors to the user
  const [loading, setLoading] = useState(false); // To disable button on submit

  const { login, user, register } = useContext(AuthContext); // Get register function from context
  const navigate = useNavigate();

  // If a user is already logged in, redirect them away from this page
  useEffect(() => {
    if (user) {
      navigate('/dashboard');
    }
  }, [user, navigate]);

  const { name, email, password } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(''); // Clear previous errors

    try {
      if (isLogin) {
        // --- LOGIN LOGIC ---
        await login(email, password);
        // The useEffect will handle the redirect automatically upon user state change
      } else {
        // --- REGISTRATION LOGIC ---
        await axios.post('http://localhost:5000/api/auth/register', { name, email, password });
        alert('Registration successful! Please log in to continue.');
        setIsLogin(true); // Switch to login form
      }
    } catch (err) {
      // Set a user-friendly error message
      setError(err.response?.data?.msg || 'An unexpected error occurred.');
    } finally {
      setLoading(false); // Re-enable the button
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h2>
          <i className={isLogin ? 'fas fa-sign-in-alt' : 'fas fa-user-plus'}></i>
          {isLogin ? 'Login' : 'Register'}
        </h2>
        <form onSubmit={onSubmit}>
          {!isLogin && (
            <div className="form-group">
              <label>Name</label>
              <input type="text" name="name" value={name} onChange={onChange} required />
            </div>
          )}
          <div className="form-group">
            <label>Email</label>
            <input type="email" name="email" value={email} onChange={onChange} required />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input type="password" name="password" value={password} onChange={onChange} required minLength="6" />
          </div>

          {/* Display error message if it exists */}
          {error && <p className="error-message">{error}</p>}

          <button type="submit" className="auth-button" disabled={loading}>
            {loading ? 'Processing...' : (isLogin ? 'Login' : 'Register')}
          </button>
        </form>
        <button onClick={() => setIsLogin(!isLogin)} className="toggle-button" disabled={loading}>
          {isLogin ? 'Need an account? Register' : 'Have an account? Login'}
        </button>
      </div>
    </div>
  );
};

export default AuthPage;
