import React, { useContext } from 'react';
import AuthContext from '../context/AuthContext';
import './ProfilePage.css';

const ProfilePage = () => {
  const { user } = useContext(AuthContext);

  if (!user) {
    return <div className="loading-text">Loading Profile...</div>;
  }

  return (
    <div className="profile-page-content">
      <h1 className="profile-title">
        <i className="fas fa-user-circle"></i> My Profile
      </h1>
      <div className="profile-card">
        <div className="profile-section">
          <h3>Account Details</h3>
          <div className="detail-item">
            <span className="detail-label">Name</span>
            <span className="detail-value">{user.name}</span>
          </div>
          <div className="detail-item">
            <span className="detail-label">Email</span>
            <span className="detail-value">{user.email}</span>
          </div>
          <div className="detail-item">
            <span className="detail-label">SkillMoney Balance</span>
            <span className="detail-value accent">{user.skillMoney} Coins</span>
          </div>
        </div>
        <div className="profile-section">
          <h3>My Skills to Teach</h3>
          <div className="skills-tags-profile">
            {user.skillsToTeach && user.skillsToTeach.length > 0 ? (
              user.skillsToTeach.map((skill) => (
                <span key={skill._id} className="skill-tag">
                  {skill.name}
                </span>
              ))
            ) : (
              <p className="detail-value">You haven't added any skills yet.</p>
            )}
          </div>
        </div>
        {/* Later, we can add an "Edit Profile" button here */}
      </div>
    </div>
  );
};

export default ProfilePage;
